function showMessage(){alert("Hello! You clicked the button.")}
function validateForm(){let email=document.getElementById("email").value;if(email===""||!email.includes("@")){alert("Please enter a valid email address.");return!1}
alert("Message sent successfully!");return!0}
function addTask(){const input=document.getElementById("taskInput");const task=input.value.trim();if(task==="")return;const listItem=document.createElement("li");listItem.innerText=task;const delBtn=document.createElement("button");delBtn.innerText="❌";delBtn.onclick=()=>{listItem.remove();saveTasks()};listItem.appendChild(delBtn);document.getElementById("taskList").appendChild(listItem);input.value="";saveTasks()}
function saveTasks(){const tasks=[];document.querySelectorAll("#taskList li").forEach(item=>{tasks.push(item.firstChild.textContent)});localStorage.setItem("tasks",JSON.stringify(tasks))}
function loadTasks(){const tasks=JSON.parse(localStorage.getItem("tasks"))||[];tasks.forEach(task=>{const listItem=document.createElement("li");listItem.innerText=task;const delBtn=document.createElement("button");delBtn.innerText="❌";delBtn.onclick=()=>{listItem.remove();saveTasks()};listItem.appendChild(delBtn);document.getElementById("taskList").appendChild(listItem)})}
window.onload=loadTasks;function getJoke(){fetch("https://official-joke-api.appspot.com/random_joke").then(res=>res.json()).then(data=>{document.getElementById("joke").innerText=data.setup+" - "+data.punchline})}
function checkAnswer(correctId,userId){if(correctId===userId){alert("Correct Answer!")}else{alert("Wrong Answer. Try Again!")}}